/*global json err websocket*/
s = new WebSocket(websocket+"/anon"), 
function auth () {
  s.onmessage = function (e) {
    data = JSON.parse(e.data), !1 === data.ok ? data.msg === 'CONF_EMAIL' ? err("Please look in your inbox for the confirmation email.") : data.msg === 'INC_DATA' ? err("Your username or password is incorrect.") : data.msg === 'NOBODY_FOUND' && err("Your username or password is incorrect.") : (document.cookie = "token=" + data.data, window.location = "index")
  }, s.send(JSON.stringify({
    cmd: "authenticate",
    data: {
      username: document.getElementById("un").value.toLowerCase(),
      password: document.getElementById("pw").value
    }
  }))
}

s.onerror = function (e) { err("Something wrong has happened with the back of the server. Please DM the administrators on discord.") },
s.onclose = function (e) { err("Something wrong has happened with the server. Please DM the admins on discord.") }
