function auth () {
  s.onmessage = function (e) {
      console.log(e)
    data = json.parse(e.data), !1 === data.ok ? data.msg === 'CONF_EMAIL' ? err('Please look in your inbox for the confirmation email.') : data.msg === 'INC_PASS' ? err('Your username or password is incorrect.') : data.msg === 'NOBODY_FOUND' && err('Your username or password is incorrect.') : (document.cookie = 'token=' + data.data, window.location = 'index')
  }, s.send(json.stringify({
    cmd: 'authenticate',
    data: {
      username: document.getElementById('un').value,
      password: document.getElementById('pw').value
    }
  }))
}
s = new WebSocket(websocket+'/anon'), 
s.onerror = function (e) { err('Something wrong has happened with the back of the server. Please DM the administrators on discord.') },
s.onclose = function (e) { err('Something wrong has happened with the server. Please DM the admins on discord.') }
